(window.webpackJsonp=window.webpackJsonp||[]).push([[39],{581:function(e,t,v){"use strict";v.r(t);var _=v(20),o=Object(_.a)({},(function(){var e=this,t=e.$createElement,v=e._self._c||t;return v("ContentSlotsDistributor",{attrs:{"slot-key":e.$parent.slotKey}},[v("p",[e._v("自学是门手艺")]),e._v(" "),v("h2",{attrs:{id:"自学是门手艺"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#自学是门手艺"}},[e._v("#")]),e._v(" 自学是门手艺")]),e._v(" "),v("ul",[v("li",[e._v("费正清的剑桥中国史（The Cambridge History of China）")])]),e._v(" "),v("p",[e._v("如若说，人间那些勾心斗角的事儿貌似从书本里学不来的 话，其实也不过还是历 史书看少了 —— 勾心斗角的套路历史上全都被反复用过了。倒是有本中文 书值得吐血推荐，民国时 代的作者连阔如先生写的《江湖丛谈》，粗略扫过你就知道了，江湖那点事 儿，也早就有人给你里 里外外翻了个遍……")]),e._v(" "),v("p",[e._v('邱老板所写的区块链交易所引擎，在 Github 上用的是个很霸气的名 字，“貔貅” （英文用了一个生造 的词，Peatio） —— 这个 Repo 至 2019 年春节的时候，总计有 2,913 个 Star，有 2,150 个 Fork…… 绝对是全球这个领域中最受关注的开源项目。2017 年 9 月，云币 应有关部门要求关闭之 前，是全球排名前三的区块链交易所。 邱老板当年上学上到几年级呢？初中未读完，所以，跟他聊天随口说成语是 很有负担的，因为他真 的可能听不懂…… 然而，他的编程、他的英语，全是自学的…… 学到什么地 步呢？学到可以创造极 有价值的商业项目的地步。他什么学习班都没上过，全靠阅读 —— 基本上 只读互联网这本大书。 讲真，你没有选择，只靠阅读习得新技能，这是你唯一的出路。 from IPython.core.interactiveshell import InteractiveShell\nInteractiveShell.ast_node_interactivity = "all"')]),e._v(" "),v("p",[e._v("预算观念非常重要 —— 这个观念的存在与否，成熟与否，基本上决定一个 人未来的盈利能力。 不夸张地讲，未来的你只需要恪守一个原则，就很可能会因此超越 99% 的 人： 绝对不做预算不够的事情。")]),e._v(" "),v("p",[e._v("手艺这个东西，尤其需要刻意练习。我们说，手艺么，主要是靠时间…… 这 里的“时间”，准确地 讲，就是“刻意练习”的时间，而不是任何时间。")]),e._v(" "),v("p",[e._v("学英语最简单的刻意 练习就是朗读。每天朗 读一小时，一百天下来就会超越绝大多数人，也会超越自己原本可能永远跨 不去的那个坎 —— 神奇 的是，朗读什么无所谓，反正现在有声书那么多…… 更神奇的是，刚开始朗 读得好不好听，甚至好 不好都无所谓，反正没几天就能体会到大幅进步…… 最神奇的是，这么简单 的事儿，99.99% 的人 不做 —— 你身在中国，能理解这个比例真的没有夸张。")]),e._v(" "),v("p",[e._v("当我在 2011 年遇到比特币的时候，现在回头看，在当时的情况下，我平日 里习惯对概念及其关联 进行各种纵向横向的拆解这件事儿给我“创造” 了巨大的好运。\n我参与过（或投资）很多失败的创业项目……\n对所有复盘的结果，无一例外，根源都是当初立项的时候，很多重要细节还 没搞清楚，甚至没想到 要去搞清楚，就已经开始行动…… 于是，在成本不断积累的情况下，没完没 了地处理各种“意外”， 没完没了地重新制定目标，没完没了地拖延，没完没了地“重新启动”…… 直至开始苟延残喘，最后 不了了之。 拆解得不够，就容易导致想不清楚，想错，想歪…… 也许，有人可能会理直气壮地反问，“怎么可能从一开始就把所有情况都想 清楚么！” 唉，是呀，以 前我也是这么想的…… 直到吃了很多亏，很多很多亏，很多很多很大很大的 亏，才“发现”且不得不 痛下决心去接收：事先想不清楚的，就不要去做。")]),e._v(" "),v("p",[e._v("这是一种特殊、且重要、又极有价值的能力。现实生活中，后来我也见过若 干有这种能力的高人， 比如，你可以到网上搜一个人名，庄辰超，他就是我见过的能做到干什么事 儿之前都能全都想清楚 的真人活人之一。 自学的时候，拆解任务的重要性更是如此。")]),e._v(" "),v("p",[e._v("我觉得绝大多数人平日里都挺大手大脚的，都挺舍不 得对自己过分苛刻的， 但，一到买书这件事儿上，绝大多数人真的很节俭，真的很苛刻 —— 对待 越严肃的知识越是如此。")]),e._v(" "),v("p",[e._v("我觉得绝大多数人平日里都挺大手大脚的，都挺舍不 得对自己过分苛刻的， 但，一到买书这件事儿上，绝大多数人真的很节俭，真的很苛刻 —— 对待 越严肃的知识越是如此。")]),e._v(" "),v("p",[e._v("有一次朋友跟我聊起他苦于没办法培养自己孩子的正经兴趣爱好…… 我说， 其实很简单，只不过是 你方法错了。你不用告诉孩子“应该学什么，应该对什么感兴趣”，而是， 想尽一切办法让孩子见识 到拥有那个技能的，令他产生羡慕情绪的人 —— 只要孩子羡慕那个人，他 就自然而然地有“我也想 这样”的想法，到最后，谁都拦不住。这就是镜像神经元的力量。进而，所 谓的社交，还真不一定是 非要跟人说话、聊天…… 见识到，也是社交的最主要组成部分。")]),e._v(" "),v("p",[e._v("Google 还要向外人强调一下，他们已经有 2500 万页面可供检 索！ 事实上，到了 2008 年，Google 公告称，可供检索页面已经超过一万亿 （One Trillion），到了\n2016 年年底，这个数字已经超过 130 万亿…… 换个角度看，这个数字同时也是互联网上信息的积累 —— 世界上再没有比 互联网更大更全 的“书”了。并且，由于 Google 的存在，互联网这本大书，是可检索的！ 于是，有事儿先问 Google 就成了自学者的必备修养。 能 Google 出答案的问题，就不需要去麻烦别人。\n这也是一个自学者的基本素养。")]),e._v(" "),v("p",[e._v("讲演能力、写作能力，其实同样也是手艺而已，但，它们也确实是很必要的 手艺。尤其是，具备这 两项手艺的人，在现在这样的社会里收入一定不差，不信你就观察一下身边\n的世界、你的眼界可以 触达的人群。")]),e._v(" "),v("p",[e._v("使用工具的技巧之一，就是用最朴素的方法使用最好的工具，这样成本最低 效果最好。所以，我在 讲课、讲演的时候，通常就只用最简单的模版，空白、单色背景那种，而后 一页里只写一句话 —— 就是接下来三五分钟里我要讲的重点（甚至干脆只是标题）…… 这样的幻灯 片，我只需要几分钟就 能做完。而后，我的所有时间精力都放在精心准备内容上去了。高效、低成 本。")]),e._v(" "),v("p",[e._v("十多年前，MIT 出了一套 Open Course，在线的免费课程。授课老师绝大 多数都是本校的著名教授 —— 在那么多课程里，我一下子就爱上了这位教授。建议你有时间把他的 所有课程看完，虽然你可 能觉得物理这东西你并不感兴趣 —— 可事实上，看完你就知道了，你只不 过是运气不好，你从来没 遇到过这么可爱牛逼的教授而已…… 在他身上，你可以学到无数，甚至 是“不可言说”的技巧、秘密 和手艺。")])])}),[],!1,null,null,null);t.default=o.exports}}]);