(window.webpackJsonp=window.webpackJsonp||[]).push([[43],{585:function(v,_,n){"use strict";n.r(_);var t=n(20),r=Object(t.a)({},(function(){var v=this,_=v.$createElement,n=v._self._c||_;return n("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[n("h1",{attrs:{id:"期货"}},[n("a",{staticClass:"header-anchor",attrs:{href:"#期货"}},[v._v("#")]),v._v(" 期货")]),v._v(" "),n("p",[v._v("《期货市场教程》\n==《期货交易技术分析》==\n《日本蜡烛图技术 》\n《专业投机原理》\n《上海期货交易所交割细则》")]),v._v(" "),n("hr"),v._v(" "),n("p",[v._v("期货交易的核心是概率思维，简单来说，就是在积极参与大概率事件的同时，努力防范小概率事件。")]),v._v(" "),n("p",[n("strong",[v._v("参与大概率事件其实分为两个过程，首先我们需要发现什么是大概率事件，其次是何时参与大概率事件。本质上前者考验的是交易者的商品选择能力，而后者考验的是交易者的择时能力。")])]),v._v(" "),n("p",[n("strong",[v._v("基本面的分析主要作用就是品种选择与方向判断，而技术分析的作用主要是进出场时机的选择。")])]),v._v(" "),n("p",[n("strong",[v._v("精明的交易者时刻保持者概率思维，他们通过基本面分析法找到了大概率事件，利用技术分析给出的明显信号来参与大概率事件；与此同时，为了防范小概率事件，他们懂得做好资金管理。为了表示对于概率思维的尊重，当他们找到自认为的大概率事件时，并不是急于入场，而是耐心等待，等待着技术分析给出自己强烈的进场信号。")])]),v._v(" "),n("hr"),v._v(" "),n("p",[v._v("1）基差数据：生意社网站\n2）库存数据：西本新干线")]),v._v(" "),n("p",[v._v("简单来说可以划分为工业品和农产品，工业品进一步划分为黑色、有色和化工三大板块，农产品可以粗略划分为油脂与白色品种两大板块。为什么需要对期货品种进行划分呢？这是因为不同品种的分析方法是不一样的，"),n("strong",[v._v("工业品主要看需求，农产品主要看供给。工业会根据需求来调整库存周期，而农产品消费相对稳定，主要看主产国的产量情况。")])]),v._v(" "),n("p",[v._v("供给与需求相互作用的结果就是库存，我们可以根据库存的大小来判断供给与需求的相对强弱。")]),v._v(" "),n("p",[v._v("与期货市场相对应的是现货市场，**所谓基差就是现货价格与期货价格之间的差额。**当期货价格大于现货价格时，我们称之为期货升水；当期货小于现货价格时，我们称之为期货贴水。"),n("strong",[v._v("由于期货交割制度的约束，理论上在交割日时，期货的价格等于现货的价格，即基差为零，否则就会出现套利的机会。")])]),v._v(" "),n("p",[n("strong",[v._v("在宏观中性的情况下，对于工业品而言，低库存且期货深度贴水时，存在做多的安全边际；高库存且期货大幅升水时，存在做空的安全边际。")]),v._v("\n**\n**\n总结一下："),n("strong",[v._v("低库存+期货深度贴水+技术信号=积极做多；高库存+期货大幅升水+技术信号=积极做空。")])]),v._v(" "),n("p",[v._v("用日线上的强力分型作为入场信号。技术入场信号是日线上明显的分型，以缠论中的分型为准。")]),v._v(" "),n("hr"),v._v(" "),n("p",[n("strong",[v._v("无论是基本面还是技术面，一切都要看盘面！")]),v._v(" "),n("strong",[v._v("对期货走势影响最为直接的三个因素：库存、基差、技术信号.")]),v._v("\n**\n**\n"),n("strong",[v._v("产业利润与宏观环境。")])]),v._v(" "),n("p",[n("strong",[v._v("高库存+期货大幅升水+技术信号=积极做空。我")]),v._v("们选择入场做空。但是如果这个产业处于严重亏损阶段，那么你会发现，期货价格并不是像我们想象中那样跌得非常顺畅。**产业长期亏损和产业暴利都是不可持续的，如果某个产业长期亏损，行业内一些落后的企业会淘汰和退出，商品供应减少，行业集中度提高，在这轮行业洗牌当中活下来的企业就会开始提高商品价格来改善利润，所以产业处于严重亏损的状态会阻碍商品的下跌。**相反，产业处于暴利的状态会阻碍商品期货的上涨。")]),v._v(" "),n("p",[n("strong",[v._v("当宏观向上，产业向下时，或者宏观向下，产业向上时，商品基本上处于一个宽幅震荡的行情。")]),v._v("\n对于黑色系的品种来说，固定资产投资、社会融资规模以及房地产销售方面的数据的情况需要关注，它们对黑色系的影响比较大；\n对于其他工业品来说，主要是出口、消费等一些需求的数据以及PMI新订单指数需要关注一下，毕竟工业品主要看需求；")]),v._v(" "),n("p",[v._v("对于农产品来说，主要看美农报告的产量数据以及国家收储与抛储相关数据。之前M2对商品的影响比较大，但现在来看，M2指标似乎有些失灵，我们需要关注利率与资本市场流动性的情况。简而言之，"),n("strong",[v._v("宏观方面主要关注投资、消费、供给、资金面四个方面即可。")])]),v._v(" "),n("p",[n("strong",[v._v("商品标的就好像是一条小船，库存和基差是船头，技术信号是船尾的船桨，产业利润是水流方向，宏观环境是风。船头朝向决定了船未来的航行放向；船桨决定了船何时开始启动；水流方向是顺流还是逆流影响了船速；风向是逆风还是顺风同样影响了船速。当船是顺流且顺风时，船桨轻轻地摇动，船就可以按照既定的方向行驶很远；当船是逆流且逆风时，船桨摇起来很累，而且船也走得并不是很远")]),v._v("。")]),v._v(" "),n("hr"),v._v(" "),n("p",[n("strong",[v._v("并不推荐散户进行农产品交易，主要是因为农产品期货当中有太多产业客户在进行套期保值和套利交易，同时由于消费基本上很稳定，主要看供给方面的数据，所以农产品很少会走出单边独立行情，基本上都是宽幅震荡行情")]),v._v("，除非由于天气或者自然灾害等情况导致农产品的供给骤减。")]),v._v(" "),n("p",[v._v("农产品的定价权在美国，所以做农产品需要关注芝加哥CBOT的期货行情，美国、巴西、阿根廷三个大豆主产国的产量情况，基本上豆油、豆粕的炒作就是天气情况，再就是美国农业部公布的相关数据。菜粕是夏季水产养殖的饲料，有明显的季节性特征，基本上夏天的时候做一波就不做了。棕榈油主要看马来西亚的库存相关数据，有时候棕榈油和豆油之间的价差套利也是不错的机会。棉花现在不如以前交易那么活跃，国家的收储与抛储政策对棉花的价格影响比较大。白糖国内产区主要是广西，而且涉及到走私的一些情况，这些不同的农产品需要特殊分析和对待。")]),v._v(" "),n("p",[n("strong",[v._v("期货的套利可分为跨期套利、产业套利、跨市场套利。")]),v._v(" "),n("strong",[v._v("跨期套利是对同一品种不同交割日期进行方向相反、数量相对的交易策略。")]),v._v(" "),n("strong",[v._v("产业套利是对同一产业链上的原材料和产成品之间的方向相反的交易策略。")]),v._v(" "),n("strong",[v._v("跨市场套利是根据一价原则，同一种商品在不同市场中价格不同，进行套利的一种策略。")])]),v._v(" "),n("p",[n("strong",[v._v("就我个人而言，我经常进行跨期套利和产业套利，我对套利的理解与传统的统计方法并不相同。我的套利基于的是逻辑判断，传统的套利策略基于的是统计分析，通常是利用回归、协整等一系列统计学中的方法进行。")])]),v._v(" "),n("p",[n("strong",[v._v("以产业套利而言，产业套利的核心就是做多或做空产业利润。如果某个产业出现高利润，由于高利润不可持续，所以这就给了我们做空产业利润进行套利的机会，做空产业利润的做法具体来说就是做多原材料同时做空产成品。相反，如果某个产业出现巨额亏损，由于高亏损不可持续，所以这就给了我们做多产业利润进行套利的机会，做多产业利润的做法具体来说就是做空原材料同时做多产成品。")])]),v._v(" "),n("p",[v._v("**以油粕比套利为例，原材料是大豆，产成品是豆粕和豆油，从量的关系上看，1单位大豆可以压榨出80%的豆粕、18%的豆油和2%的损失。然而需要注意的是，如果你是在芝加哥CBOT市场上进行交易，完全可以按照上面我所说的产业套利的逻辑进行操作，因为美国大豆是转基因的产品，我国每年需要从美国进口大量大豆，主要利用这些大豆进行压榨来形成产业利润，而国内豆二是转基因大豆，但是持仓量非常低，不利于交易。所以我们在进行产业套利时，需要转换思路。我们可以从产成品来做文章，由于豆粕和豆油的产量比是80%比18%，接近4.5倍的比例关系，而豆油的价格仅为豆粕价格的2-3倍，所以压榨企业的主要收入来源还是豆粕。因此，做多豆粕、做空豆油是一种变相做多产业利润，做空豆粕、做多豆油是一种变相做空产业利润。**在2016年的时候，压榨企业亏损严重，我抓住了这个时机，多豆粕、空豆油，采取吨对吨的持仓数量，结果多单和空单都赚钱，给我带来了7位数的收入。")]),v._v(" "),n("p",[v._v("当然，产业客户通常是进行双边跨市场套利，他们时刻关注CBOT的美豆价格，然后根据汇率和基差计算进口大豆的到岸成本，从而计算出进口大豆压榨利润，如果有利可图，他们就从CBOT买入，在大商所卖出。")]),v._v(" "),n("hr"),v._v(" "),n("p",[v._v("我们交易的标的是商品期货，而商品本身存在库存周期。这个周期简单来说分为两个阶段：**库存重建过程中的价格上涨阶段和去库存过程中的价格下跌阶段。**这才是趋势的本质。")]),v._v(" "),n("p",[n("strong",[v._v("当市场价格开始下跌时，资金是矛盾的主要方面，经销商将商品资源转化为资金，而终端用户则延缓资金采购商品资源的进程，所以库存下降的同时，价格随之快速下跌。相反，当市场价格上涨时，商品资源是市场的主要矛盾，经销商急于将资金转化为商品资源，而终端用户则将资金用来采购货源，从而形成库存量上升的同时，价格快速上涨")]),v._v("。")])])}),[],!1,null,null,null);_.default=r.exports}}]);